import React, { useEffect } from 'react';
import AppLayout from '../components/AppLayout';
import LoginForm from '../components/LoginForm';
import { Typography } from 'antd';
import { ImageLayout } from '../components/style';
const Title = Typography.Title;
import { useSelector } from 'react-redux';
import { END } from 'redux-saga';
import axios from 'axios';
import wrapper from '../store/configureStore';
import { LOAD_MY_INFO_REQUEST, } from '../reducers/user';
import Router from 'next/router';
import { initialState } from "../reducers/user";
import useSWR, { mutate, trigger } from "swr";
const Login = () => {
    const me = useSelector(state => state.user.me);
    const logOutLoading = useSelector(state => state.user.logOutLoading);
    const { data: menuKeyData } = useSWR("globalState", { initialData: initialState }, { revalidateOnFocus: true });
    useEffect(() => {
        if (me && me.id) {
            mutate("globalState", {
                ...menuKeyData,
                me: { menuKey: '1' }
            }, false);
            Router.replace('/');
            trigger("globalState");
        }
    }, [me]);

    return (
        <AppLayout>
            {me && me.id ? (
                <>
                    <ImageLayout />
                    <Title level={3}>{!logOutLoading ? (`${me.nickname}님, 환영합니다!`) : (`${me.nickname}님, 다시 또 만나요!`)}</Title>
                    <div className='img-dancer-position-three'>
                        <img
                            width={600}
                            height={600}
                            src="/images/dancer2.png"
                        /></div>
                </>
            ) : <LoginForm />}
        </AppLayout>
    );
};

export const getServerSideProps = wrapper.getServerSideProps(async (context) => {

    const cookie = context.req ? context.req.headers.cookie : '';
    axios.defaults.headers.Cookie = '';
    if (context.req && cookie) {
        axios.defaults.headers.Cookie = cookie;
    }
    context.store.dispatch({
        type: LOAD_MY_INFO_REQUEST,
    });
    context.store.dispatch(END);

    await context.store.sagaTask.toPromise();
});
export default Login;
