import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import dummyUser from '../swr/user';
import dummyPost from '../swr/post';
import initialStore from '../reducers/store';
import useSWR, { mutate } from 'swr';
import produce from '../utils/produce';
import AppLayout from '../components/AppLayout';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';

const Home = () => {

    const { userData } = useSWR("dummyUserState", { initialData: dummyUser });

    const [me, setMe] = useState((userData || {}).me);

    if (!userData) null;

    console.log(mainPosts);
    // const { me } = useSelector(state => state.user);
    // const { mainPosts } = useSelector(state => state.post);

    function Profile() {
        const { postData } = useSWR("dummyPostState", { initialData: dummyPost });
        console.log(postData);
        const [mainPosts, setmainPosts] = useState((postData || {}).mainPosts);
        if (!postData) null;
        const { data } = useSWR("globalState", { initialData: initialStore });

        const [value, updateValue] = useState((data || {}).name);
        const nextState = produce(data, (draft) => {

            draft.name = value;
        });
        if (!data) {
            return null;
        }
        return (
            <div>
                <h1>my name is {data.name}</h1>
                <input
                    value={value}
                    onChange={e => updateValue(e.target.value)}
                    style={{ width: 200, marginRight: 8 }} />
                <button
                    type="button"
                    onClick={() => {
                        mutate("globalState", nextState, false)
                    }}>uppercase my name</button>
            </div>
        );

    }

    function Other() {
        const { data } = useSWR("globalState", {
            initialData: initialStore
        });
        if (!data) null;
        return (
            <div style={{ border: "1px solid #ddd", marginTop: 30, padding: 20 }}>
                <h1>another components:<br />
                my name is {data.name}.</h1>
            </div>
        );
    }

    return (
        <AppLayout>
            <div style={{ padding: 40 }}>
                useSWR components:
    <Profile />
                <Other />
            </div>
            {me && <PostForm />}
            {/* {mainPosts.map((c) => {
                return (
                    <PostCard key={c.id} post={c} />
                );
            })} */}
            {mainPosts}
        </AppLayout>
    );
};
export default Home;
