    //             ...menuKeyData,
    //             me: { menuKey: '1' }
    //         }, false);
    //         Router.replace('/');
    //         trigger("globalState");
    //     }
    // }, [logInDone, logOutDone]);

    return (
        <AppLayout>
            {me && me.id ? (
                <>
                    <ImageLayout />
                    <div className='img-dancer-position'>
                        <Title level={3}>{`${me.nickname}님, Y Milonga에 오신 걸 환영합니다!`}</Title>
                        <div>
                            <img
                                width={500}
                                height={500}
                                src="/images/dancer2.png"
                            /></div>
                    </div>
                </>
            ) : <LoginForm />}
        </AppLayout>
    );
};

export default Login;
