import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import initialStore from '../reducers/user';
import useSWR, { mutate } from 'swr';

import AppLayout from '../components/AppLayout';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';

const Home = () => {


    const { me } = useSelector(state => state.user);
    const { mainPosts } = useSelector(state => state.post);

    function Profile() {
        const { data } = useSWR("state", { initialData: initialStore });
        const [value, updateValue] = useState((data || {}).name);
        if (!data) {
            return null;
        }
        return (
            <div>
                <h1>my name is {data.name}</h1>
                <input
                    value={value}
                    onChange={e => updateValue(e.target.value)}
                    style={{ width: 200, marginRight: 8 }} />
                <button
                    type="button"
                    onClick={() => {
                        mutate("state", { ...data, name: value }, false)
                    }}>uppercase my name</button>
            </div>
        );

    }

    function Other() {
        const { data } = useSWR("state", {
            initialData: initialStore
        });
        if (!data) null;
        return (
            <div style={{ border: "1px solid #ddd", marginTop: 30, padding: 20 }}>
                <h1>another components:<br />
                my name is {data.name}.</h1>
            </div>
        );
    }

    return (
        <AppLayout>
            <div style={{ padding: 40 }}>
                useSWR components:
    <Profile />
                <Other />
            </div>
            {me && <PostForm />}
            {mainPosts.map((c) => {
                return (
                    <PostCard key={c.id} post={c} />
                );
            })}
        </AppLayout>
    );
};
export default Home;
