import React, { useCallback } from 'react';
import { Button, List, Avatar } from 'antd';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { useDispatch } from 'react-redux';
import { UNFOLLOW_REQUEST, REMOVE_FOLLOWER_REQUEST } from '../reducers/user';

const FollowList = ({ header, data, onClickMore }) => {
    console.log(header);
    const dispatch = useDispatch();
    const onCancel = (id) => () => {
        console.log(id);
        if (header === 'following') {
            dispatch({
                type: UNFOLLOW_REQUEST,
                data: id
            });
        }
        dispatch({
            type: REMOVE_FOLLOWER_REQUEST,
            data: id
        });
    };
    const loadMore = (
        <div
            style={{
                textAlign: 'center',
                marginTop: 12,
                height: 32,
                lineHeight: '32px',
            }}
        >
            <Button onClick={onClickMore}>더보기</Button>
        </div>
    );
    return (

        <List
            header={<div>{header}</div>}
            dataSource={data}
            itemLayout="horizontal"
            loadMore={loadMore}
            bordered
            renderItem={(item) => (
                <List.Item>
                    <List.Item.Meta
                        avatar={
                            (<Link href={`/user/${item.id}`}>
                                <a><Avatar>
                                    {item.nickname[0]}
                                </Avatar></a>
                            </Link>)
                        }
                        title={item.nickname}
                    />
                    <div>
                        <Link href="/post" style={{ marginRight: '2px' }}>
                            <a key="post">게시글 보기</a>
                        </Link>
                        <div>
                            <a key="stop" onClick={onCancel(item.id)}>연결 끊기</a>
                        </div>
                    </div>
                </List.Item>
            )}
        />

    );
};

FollowList.propTypes = {
    header: PropTypes.string.isRequire,
    data: PropTypes.array.isRequire,
    onClickMore: PropTypes.func.isRequire,

}
export default FollowList;
