import React, { useCallback, useState } from 'react';
import Link from 'next/link';
import { Avatar, Card, Skeleton, Divider } from 'antd';
import { } from '@ant-design/icons';

import NicknameEditForm from '../components/NicknameEditForm';

const UserProfile = ({ setIsLoggedIn }) => {
    const [editNickname, setEditNickname] = useState(false);
    const onClickEditNickname = useCallback(() => {
        setEditNickname((prev) => !prev);
    }, []);

    const onLogout = useCallback(() => {
        setIsLoggedIn(false);
    }, []);
    return (
        <>
            <Card
                style={{ width: 300, marginTop: 16 }}
                type="inner"
                extra={[
                    <a key="logout" onClick={onLogout}>로그아웃</a>,
                    <Divider type="vertical" />,
                    <a key="edit-nickname" onClick={onClickEditNickname}>닉네임 수정하기</a>
                ]}
                actions={[
                    <div key="twit"><Link href="/"><a>게시글<br />1</a></Link></div>,
                    <div key="following"><Link href="/"><a>팔로잉<br />1</a></Link></div>,
                    <div key="follower"><Link href="/"><a>팔로어<br />1</a></Link></div>,
                ]}
            >
                <Skeleton>

                    <Card.Meta
                        avatar={
                            (<Link><a><Avatar></Avatar></a></Link>)
                        }
                        title="Card title"
                    />
                </Skeleton>
                {editNickname && <NicknameEditForm />}
            </Card>
        </>
    );
};
export default UserProfile;
