import React, { useState } from 'react';
import initialStore from '../reducers/store';
import useSWR, { mutate } from 'swr';
import produce from '../utils/produce';

import fetch from '../libs/fetch'
import AppLayout from '../components/AppLayout';
import PostForm from '../components/PostForm';
import PostCard from '../components/PostCard';
import useMe from '../hooks/useMe';
import useMainPosts from '../hooks/useMainPosts';
const Home = () => {
    const [text, setText] = React.useState('');
    const { data } = useSWR('/api/data', fetch)

    async function handleSubmit(event) {
        event.preventDefault()
        // call mutate to optimistically update the UI
        // we use Immer produce to allow us to perform and immutable change
        // while coding it as a normal mutation of the same object
        mutate("/api/data", produce(draftData => {
            draftData.push(text)
        }), false)
        // then we send the request to the API and let mutate
        // update the data with the API response
        // if this fail it will rollback the optimistic update
        mutate('/api/data', await fetch('/api/data', {
            method: 'POST',
            body: JSON.stringify({ text })
        }))
        setText('')
    }

    const [me] = useMe();
    const [mainPosts] = useMainPosts();
    function Profile() {

        const { data } = useSWR("globalState", { initialData: initialStore });

        const [value, updateValue] = useState((data || {}).name);
        const nextState = produce(data, (draft) => {

            draft.name = value;
        });
        if (!data) {
            return null;
        }
        return (
            <div>
                <h1>my name is {data.name}</h1>
                <input
                    value={value}
                    onChange={e => updateValue(e.target.value)}
                    style={{ width: 200, marginRight: 8 }} />
                <button
                    type="button"
                    onClick={() => {
                        mutate("globalState", nextState, false)
                    }}>uppercase my name</button>
            </div>
        );

    }

    function Other() {
        const { data } = useSWR("globalState", {
            initialData: initialStore
        });
        if (!data) null;
        return (
            <div style={{ border: "1px solid #ddd", marginTop: 30, padding: 20 }}>
                <h1>another components:<br />
                my name is {data.name}.</h1>
            </div>
        );
    }
    return (

        <AppLayout>
            <div>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        onChange={event => setText(event.target.value)}
                        value={text}
                    />
                    <button>Create</button>
                </form>
                <ul>
                    {data ? data.map(datum => <li key={datum}>{datum}</li>) : 'loading...'}
                </ul>
            </div>
            <div style={{ padding: 40 }}>
                useSWR can share state between components:
        <Profile />
                <Other />
            </div>
            {me && <PostForm />}
            {mainPosts.map((c) => {
                return (
                    <PostCard key={c.id} post={c} />
                );
            })}

        </AppLayout>
    );
};
export default Home;
