import React, { useCallback } from 'react';
import { Button, List, Avatar } from 'antd';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { useSelector, useDispatch } from 'react-redux';
import { UNFOLLOW_REQUEST } from '../../../nodebird/react-nodebird/ch4/front/reducers/user';

const FollowList = ({ header, data, onClickMore }) => {
    const { } = useSelector(state => state.user);
    const dispatch = useDispatch();
    const onCancel = useCallback((id) => () => {
        if (header === 'following') {
            console.log('following');
            dispatch({
                type: UNFOLLOW_REQUEST,
                data: id
            });
        }
        console.log('follower');
    }, []);
    const loadMore = (
        <div
            style={{
                textAlign: 'center',
                marginTop: 12,
                height: 32,
                lineHeight: '32px',
            }}
        >
            <Button onClick={onClickMore}>more</Button>
        </div>
    );
    return (

        <List
            header={<div>{header}</div>}
            dataSource={data}
            itemLayout="horizontal"
            loadMore={loadMore}
            renderItem={item => (
                <List.Item
                    actions={[
                        <Link href={`/post/${item.User.id}`}>  <a key="post">게시글 보기</a> </Link>,
                        <a key="stop" onClick={onCancel(item.id)}>연결 끊기</a>]}
                >

                    <List.Item.Meta
                        avatar={
                            (<Link href={`/user/${item.id}`}><a><Avatar></Avatar></a></Link>)
                        }
                        title={item.nickname}
                    />


                </List.Item>
            )}
        />

    );
};

FollowList.propTypes = {
    header: PropTypes.string.isRequire,
    data: PropTypes.array.isRequire,
    onClickMore: PropTypes.func.isRequire,

}
export default FollowList;
