import React, { useRef, useCallback, useState } from 'react';

import { Form, Input, Button } from 'antd';

import useImagePaths from '../hooks/useImagePaths';
import useMainPosts from '../hooks/useMainPosts';
import useInput from '../hooks/useInput';
import { mutate } from 'swr';



const PostForm = () => {

    const [mainPosts, setMainPosts] = useMainPosts();
    const [imagePaths] = useImagePaths();
    console.log('imagePaths', imagePaths);
    const [text, onChangeText, setText] = useInput('');
    const imageInput = useRef();
    // const onChangeText =useCallback(()=>{

    // },[]);
    const onChangeImages = useCallback((e) => {
        console.log('clickImgInfo', e.target.files);
        const imageFormData = new FormData();
        [].forEach.call(e.target.files, (f) => {
            imageFormData.append('image', f);
        })
    }, []);

    const onClickImageUpload = useCallback(() => {
        imageInput.current.click();
    }, [imageInput.current]);

    const onRemoveImage = useCallback((index) => () => {

    }, []);

    const nextPostsState = produce(draft => { });
    const onSubmit = useCallback(() => {
        if (!text || !text.trim()) alert('게시글을 작성하세요.');
        const FormData = new FormData();
        imagePaths.forEach((p) => {
            FormData.append('image', p);
        });
        FormData.append('content', text);
        mutate("dummyPostState", nextPostsState, false);
    }, [text, imagePaths]);
    return (

        <Form
            style={{ margin: '10px 0 20px' }}
            encType="multipart/form-data"
            onFinish={onSubmit}
        >
            <Input.TextArea
                value={text}
                onChange={onChangeText}
                maxLength={140}
                placeholder="오늘은 어떤 일이 있었나요?"
            />
            <div>
                <input type="file" name="image" multiple hidden
                    ref={imageInput} onChange={onChangeImages} />
                <Button onClick={onClickImageUpload}>이미지 업로드하기</Button>
                <Button type="primary" style={{ float: 'right' }}
                    htmlType="submit">게시하기</Button>
            </div>
            <div>
                {imagePaths.map((v, i) => {
                    return (
                        <div key={v} style={{ display: 'inline-block' }}>
                            <img src={v} style={{ width: '200px' }} alt={v} />
                            <div>
                                <Button
                                    onClick={onRemoveImage(i)}>삭제하기</Button>
                            </div>
                        </div>
                    );
                })}
            </div>
        </Form>
    );
};
export default PostForm;
