            //         <List.Item.Meta
            //             avatar={
            //                 (<Link><a><Avatar></Avatar></a></Link>)
            //             }
            //             title="{item.nickname}"
            //         />
            //         <>content</>

            //     </List.Item>
            // )}
            />
        </>
    );
};
export default FollowList;
