import React, { useCallback, useState } from 'react';
import Link from 'next/link';
import { Avatar, Card, Divider } from 'antd';
import { LogoutOutlined, SettingOutlined } from '@ant-design/icons';
import { CardWrapper } from './style';
import NicknameEditForm from '../components/NicknameEditForm';
import { useSelector, useDispatch } from 'react-redux';
import { LOG_OUT_REQUEST } from '../../../nodebird/react-nodebird/ch4/front/reducers/user';

const UserProfile = () => {
    const dispatch = useDispatch();
    const { me } = useSelector(state => state.user);
    const [editNickname, setEditNickname] = useState(false);
    const onClickEditNickname = useCallback(() => {
        setEditNickname((prev) => !prev);
    }, []);

    const onLogout = useCallback(() => {
        dispatch({
            type: LOG_OUT_REQUEST
        });
    }, []);
    return (
        <CardWrapper>
            <Card
                style={{ width: 300, marginTop: 16 }}
                type="inner"
                extra={[
                    <LogoutOutlined key="logout" onClick={onLogout} />,
                    <Divider type="vertical" />,
                    <SettingOutlined key="edit-nickname" onClick={onClickEditNickname} />
                ]}
                actions={[
                    <div key="twit"><Link href="/post"><a>게시글<br />{me.Posts.length}</a></Link></div>,
                    <div key="following"><Link href="/post/followings"><a>팔로잉<br />{me.Followings.length}</a></Link></div>,
                    <div key="follower"><Link href="post/followers"><a>팔로어<br />{me.Followers.length}</a></Link></div>,
                ]}
            >
                <Card.Meta
                    avatar={
                        (<Link
                            href="/profile"><a><Avatar>
                                {me.nickname[0]}</Avatar></a></Link>)
                    }
                    title="Card title"
                />
                {editNickname && <NicknameEditForm />}
            </Card>
        </CardWrapper>
    );
};
export default UserProfile;
