import React, { useState, useCallback } from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import { Menu, Button, Switch, Divider, Row, Col } from 'antd';
import {
    HomeOutlined,
    MenuUnfoldOutlined,
    MenuFoldOutlined,
    AimOutlined,
    UserOutlined
} from '@ant-design/icons';
import styled from '@emotion/styled';

const toggleButton = styled(Button)`
margin: 10px;
`;

const AppLayout = ({ children }) => {
    const [collapsed, setCollapsed] = useState(false);
    const [theme, setTheme] = useState('light');

    const onChangeTheme = value => {
        setTheme(value ? 'dark' : 'light');
    };


    const toggleCollapsed = useCallback(() => {
        setCollapsed((prev) => !prev);
    }, []);
    return (

        <div style={{ width: 256 }}>
            <toggleButton type="primary" onClick={toggleCollapsed} >
                {React.createElement(collapsed ? MenuUnfoldOutlined : MenuFoldOutlined)}
            </toggleButton>
            <Divider type="vertical" />
            <Switch
                onChange={onChangeTheme}
            />Dark Mode
            <br />
            <br />
            <Menu
                theme={theme}
                defaultSelectedKeys={['1']}
                inlineCollapsed={collapsed}
            >
                <Menu.Item key="1" icon={<HomeOutlined />}>
                    <Link href="/"><a>Home</a></Link>
                </Menu.Item>

                <Menu.Item key="2" icon={<AimOutlined />}>
                    <Link href="/map"><a>Map</a></Link> </Menu.Item>
                <Menu.Item key="3" icon={<UserOutlined />}>
                    <Link href="/profile"><a>Profile</a></Link> </Menu.Item>
            </Menu>
            <Row gutter={4}>
                <Col>
                </Col>
            </Row>
            {children}
        </div>
    );
};
AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
};
export default AppLayout;
